#define LOCKED_IMAGE ""

#define UNLOCKED_IMAGE ""

@interface FOTRButton()
{
	NSImage * unlockedImage, * lockedImage;
	FGOChatViewController * _controller;
}
@end

@interface FOTRButtonCell : NSButtonCell
@end

#define BORDER 19

@implementation FOTRButton

- (instancetype) initButtonWithController : (FGOChatViewController *) controller
{
	self = [self initWithFrame:NSMakeRect(0, 0, BORDER, BORDER)];
	
	if(self != nil)
	{
		//Load the base64, embed, image into a nice NSImage
		NSData * rawImage = [[NSData alloc] initWithBase64EncodedString:@UNLOCKED_IMAGE
																options:NSDataBase64DecodingIgnoreUnknownCharacters];
		
		if(rawImage != nil)			//Unlocked lock image
			unlockedImage = [[NSImage alloc] initWithData: rawImage];
		
		rawImage = [[NSData alloc] initWithBase64EncodedString:@LOCKED_IMAGE
													   options:NSDataBase64DecodingIgnoreUnknownCharacters];

		if(rawImage != nil)			//Locked lock image
			lockedImage = [[NSImage alloc] initWithData: rawImage];
		
		//If everything was properly loaded, we finish the initialization
		if(lockedImage != nil && unlockedImage != nil)
		{
			unlockedImage.size = lockedImage.size = NSMakeSize(BORDER, BORDER);
			_controller = controller;
			
			self.focusRingType = NSFocusRingTypeNone;
			[(NSButtonCell *) self.cell setImage:unlockedImage];
			
			self.bordered = NO;
			self.wantsLayer = NO;
			self.target = self;
			self.action = @selector(buttonClick);
		}
		else
		{
			NSLog(@"Could't initialize the lock images, aborting :(");
			self = nil;
		}
	}
	
	return self;
}

+ (Class) cellClass
{
	return [FOTRButtonCell class];
}

#pragma mark - Handle superview resizing

- (void) viewWillMoveToSuperview:(NSView *)newSuperview
{
	if(self.superview != nil)
	{
		self.superview.postsFrameChangedNotifications = NO;
		[[NSNotificationCenter defaultCenter] removeObserver:self name:NSViewFrameDidChangeNotification object:self.superview];
	}

	newSuperview.postsFrameChangedNotifications = YES;
	[[NSNotificationCenter defaultCenter] addObserver:self selector:@selector(needUpdateOrigin) name:NSViewFrameDidChangeNotification object:newSuperview];
}

- (void) viewDidMoveToSuperview
{
	[self needUpdateOrigin];
}

- (void) needUpdateOrigin
{
	if(self.superview != nil)
	{
		NSRect frame = self.superview.frame;
		NSSize buttonSize = self.bounds.size;
		
		frame.origin.x = frame.size.width - buttonSize.width - 15;
		frame.origin.y = frame.size.height / 2 - buttonSize.height / 2;
		[self setFrameOrigin:frame.origin];
	}
}

#pragma mark - Status update

- (void) setLocked:(BOOL)locked
{
	if(locked != _locked)
	{
		_locked = locked;
		
		if(locked)
			[(NSButtonCell *) self.cell setImage:lockedImage];
		else
			[(NSButtonCell *) self.cell setImage:unlockedImage];
	}
}

- (void) buttonClick
{
	if(self.coreListener != nil)
	{
		if(self.locked)
			[self.coreListener needDisableOTRForConversation:_controller fromButton:self];
		else
			[self.coreListener needActivateOTRForConversation:_controller fromButton:self];
	}
}

@end

@implementation FOTRButtonCell

- (void) highlight:(BOOL)flag withFrame:(NSRect)cellFrame inView:(NSView*)controlView
{
	if(flag)
		self.backgroundColor = [NSColor clearColor];

	[super highlight:flag withFrame:cellFrame inView:controlView];
}


@end
